import React, {useEffect} from 'react';
import clsx from 'clsx';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import List from '@material-ui/core/List';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import Badge from '@material-ui/core/Badge';
import Container from '@material-ui/core/Container';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

import MenuIcon from '@material-ui/icons/Menu';
import Drawer from '@material-ui/core/Drawer';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import { mainListItems, secondaryListItems } from './listItems';

import { makeStyles } from '@material-ui/core/styles';

import Chart from './Chart';
import Details from './Details';


import './App.css';
import { Button } from '@material-ui/core';


import FormControl from '@material-ui/core/FormControl';
import FormHelperText from '@material-ui/core/FormHelperText';


import OutlinedInput from '@material-ui/core/OutlinedInput';

import InputLabel from '@material-ui/core/InputLabel';

import Select from '@material-ui/core/Select';

import NativeSelect from '@material-ui/core/NativeSelect';

import Icon from '@mdi/react'
import { mdiAccountBadgeHorizontalOutline } from '@mdi/js'

const drawerWidth = 240;
const useStyles = makeStyles(theme => ({
  root: {
    display: 'flex',
  },
  toolbar: {
      paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },

  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(2),
    display: 'flex',
    overflow: 'auto',
    flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
  buttonHeight: {
    height: 55,
  },

}));


function App() {
  useEffect(() => {
    fetch('http.localhost:5000/user_json',{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
      })
      .then ((response) => response.json())
      .then((data) => {
      console.log(data); 
    })
    // .then(res => res.text())          // convert to plain text
    // .then(text => console.log(text))

  }, [])
  const classes = useStyles();
  const [open, setOpen] = React.useState(true);
  const handleDrawerOpen = () => {
    setOpen(true);
  };
  const handleDrawerClose = () => {
    setOpen(false);
  };
  const fixedHeightPaper = clsx(classes.paper, classes.fixedHeight);

  const [state, setState] = React.useState({
    indicator: ''
  });

  const inputLabel = React.useRef(null);

  const [labelWidth, setLabelWidth] = React.useState(0);
  React.useEffect(() => {
    setLabelWidth(inputLabel.current.offsetWidth);
  }, []);


  const handleChange = name => event => {
    setState({
      ...state,
      [name]: event.target.value,
    });
  };

  return (
    <div className={classes.root}>
      <AppBar position="absolute"  className={clsx(classes.appBar, open && classes.appBarShift)}>
        <Toolbar className={classes.toolbar}>
          <IconButton
            edge="start"
            color="inherit"
            aria-label="Open drawer"
            onClick={handleDrawerOpen}
            className={clsx(classes.menuButton, open && classes.menuButtonHidden)}
          >
            <MenuIcon />
          </IconButton>
          <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
            Health Monitor
          </Typography>
          <IconButton color="inherit">
            <Badge> 
              <Icon path={mdiAccountBadgeHorizontalOutline}
                size={1.5}
                horizontal                
                color="rgb(255, 255, 255)"
              />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>

      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !open && classes.drawerPaperClose),
        }}
        open={open}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <Divider />
        <List>{mainListItems}</List>
        <Divider />
        <List>{secondaryListItems}</List>
      </Drawer>



      <main className={classes.content}>
        <div className={classes.appBarSpacer} />
        <Container maxWidth="lg" className={classes.container}>
          <Grid container spacing={3}>
            <Grid 
              container
              item xs={1.5}
              direction="row"
              justify="space-between"
              alignItems="flex-start"
            >               
              {/* <FormControl className={classes.formControl}>
                <NativeSelect
                  className={classes.selectEmpty}
                  value={state.indicator}
                  name="indicator"
                  onChange={handleChange('indicator')}
                >                 
                  <option value={10}>Indicator 1</option>
                  <option value={20}>Indicator 2</option>
                  <option value={30}>Indicator 3</option>
                </NativeSelect>
                <FormHelperText>Choose an indicator</FormHelperText>
              </FormControl> */}

            <FormControl variant="outlined" className={classes.formControl}>
              <InputLabel ref={inputLabel} htmlFor="outlined-age-native-simple">
                Indicator
              </InputLabel>
              <Select
                native
                value={state.indicator}
                onChange={handleChange('indicator')}
                input={
                  <OutlinedInput name="indicator" labelWidth={labelWidth} id="outlined-age-native-simple" />
                }
              >
                <option value="" />
                <option value={10}>Slow acting insulin (morning intake dose)</option>
                <option value={20}>Fast acting insulin (morning intake dose)</option>
                <option value={30}>Fast acting insulin (lunch intake dose)</option>
                <option value={30}>Fast acting insulin (dinner intake dose)</option>
              </Select>
            </FormControl>


              <Button variant="outlined" size="large" color="primary" className={classes.buttonHeight}>ADD NEW DATA</Button> 


            </Grid>
            {/* Chart */}
            <Grid item xs={12}>
              <Paper className={fixedHeightPaper}>
                <Chart />
              </Paper>
            </Grid>

            {/* Data */}
            <Grid item xs={12}>
              <Paper className={classes.paper}>
                <Details />
              </Paper>
            </Grid>
          </Grid>
        </Container>
      </main>

    </div>
  );
}

export default App;
